(function($){Galleria.addTheme({name:'idg-light',author:'Galleria',css:'galleria.idg-light.css',defaults:{transitionInitial:'fade',transition:'slide',thumbnails:false,idleTime:1800,_toggleInfo:false},init:function(options){this.addElement('info-link','info-close');this.append({'info':['info-link','info-close']});$(".galleria-container").hover(function(){$('.galleria-info').addClass("info-active");},function(){$('.galleria-info').removeClass("info-active");})
var info=this.$('info-link,info-close,info-text'),touch=Galleria.TOUCH,click=touch?'touchstart':'click';this.$('loader,counter').show().css('opacity',1);if(!touch){this.addIdleState(this.get('image-nav-left'),{opacity:0.8});this.addIdleState(this.get('image-nav-right'),{opacity:0.8});this.addIdleState(this.get('counter'),{opacity:0.5});}
if(options._toggleInfo===true){info.bind(click,function(){info.toggle();});}else{info.show();this.$('info-link, info-close').hide();}
this.bind('thumbnail',function(e){if(!touch){$(e.thumbTarget).css('opacity',0.6).parent().hover(function(){$(this).not('.active').children().stop().fadeTo(100,1);},function(){$(this).not('.active').children().stop().fadeTo(400,0.6);});if(e.index===options.show){$(e.thumbTarget).css('opacity',1);}}});this.bind('loadstart',function(e){if(!e.cached){this.$('loader').show().fadeTo(200,0.4);}
this.$('info').toggle(this.hasInfo());$(e.thumbTarget).css('opacity',1).parent().siblings().children().css('opacity',0.6);});this.bind('loadfinish',function(e){this.$('loader').fadeOut(200);});}});}(jQuery));